import React, { useRef, useEffect, useState } from "react";
import CanvasDraw from 'react-canvas-draw';
import "./App.css";

function App() {
  const firstCanvas = useRef(null);
  const [images, setImages] = useState([]);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [dragStartPos, setDragStartPos] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [imageTransforms, setImageTransforms] = useState([]); // Estado para las transformaciones de las imágenes

  useEffect(() => {
    const handlePaste = (event) => {
      const items = (event.clipboardData || event.originalEvent.clipboardData).items;
      console.log('Items:', items); // Registro para depuración
      for (let index in items) {
        const item = items[index];
        if (item.kind === 'file') {
          const blob = item.getAsFile();
          const reader = new FileReader();
          reader.onload = (e) => {
            console.log('Image loaded:', e.target.result); // Registro para depuración
            setImages(prevImages => [...prevImages, { src: e.target.result, x: 0, y: 0 }]);
            setImageTransforms(prevTransforms => [...prevTransforms, { x: 0, y: 0 }]);
          };
          reader.readAsDataURL(blob);
        }
      }
    };

    document.addEventListener('paste', handlePaste);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    return () => {
      document.removeEventListener('paste', handlePaste);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [dragging, dragStartPos, selectedImageIndex]);

  const handleMouseDown = (event, index) => {
    setSelectedImageIndex(index);
    setDragStartPos({ x: event.clientX, y: event.clientY });
    setDragging(true);
  };

  const handleMouseMove = (event) => {
    if (dragging && selectedImageIndex !== null) {
      const deltaX = event.clientX - dragStartPos.x;
      const deltaY = event.clientY - dragStartPos.y;
      setImageTransforms(prevTransforms => {
        const updatedTransforms = [...prevTransforms];
        updatedTransforms[selectedImageIndex] = {
          x: updatedTransforms[selectedImageIndex].x + deltaX,
          y: updatedTransforms[selectedImageIndex].y + deltaY
        };
        return updatedTransforms;
      });
      setDragStartPos({ x: event.clientX, y: event.clientY });
    }
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  const clear = () => {
    firstCanvas.current.clear();
    setImages([]);
    setImageTransforms([]);
  };

  const undo = () => {
    firstCanvas.current.undo();
  };

  const downloadWithBlankBackground = () => {
    const canvas = firstCanvas.current.canvas.drawing;
    const tempCanvas = document.createElement('canvas');
    tempCanvas.width = canvas.width;
    tempCanvas.height = canvas.height;
    const tempCtx = tempCanvas.getContext('2d');

    // Dibujar fondo blanco en el canvas temporal
    tempCtx.fillStyle = '#ffffff'; // Color blanco
    tempCtx.fillRect(0, 0, tempCanvas.width, tempCanvas.height);

    // Dibujar el contenido del lienzo en el canvas temporal
    tempCtx.drawImage(canvas, 0, 0);

    // Dibujar las imágenes en el canvas temporal
    images.forEach((image, index) => {
      const img = new Image();
      img.src = image.src;
      tempCtx.drawImage(img, imageTransforms[index].x, imageTransforms[index].y);
    });

    // Convertir el canvas temporal en una URL de datos en formato PNG
    const dataURL = tempCanvas.toDataURL('image/png');

    // Descargar la imagen
    downloadDataURL(dataURL, 'tablero.png');
  };

  const downloadDataURL = (dataURL, filename) => {
    const link = document.createElement('a');
    link.href = dataURL;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={{ position: 'relative' }}>
      <div
        style={{
          position: 'absolute',
          top: 10,
          left: 10,
          zIndex: 1000
        }}
      >
        <button onClick={clear}>
          Limpiar Dibujo
        </button>
        <button onClick={undo}>
          Deshacer
        </button>
        <button onClick={downloadWithBlankBackground}>
          Descargar imagen
        </button>
      </div>
      <CanvasDraw
        brushRadius={3}
        brushColor='red' // Color del pincel
        canvasWidth={1500} // Ancho del lienzo
        canvasHeight={1000} // Alto del lienzo
        hideGrid={false}
        style={{ border: '1px solid black' }}
        ref={firstCanvas}
      />
      {images.map((image, index) => (
        <img
          key={index}
          src={image.src}
          alt="pasted"
          className="pasted-image"
          style={{
            transform: `translate(${imageTransforms[index]?.x}px, ${imageTransforms[index]?.y}px)`,
            border: selectedImageIndex === index ? '2px solid blue' : 'none'
          }}
          onMouseDown={(event) => handleMouseDown(event, index)}
        />
      ))}
    </div>
  );
}

export default App;
