import React, { useRef, useEffect, useState } from "react";
import CanvasDraw from 'react-canvas-draw';
import penIcon from "./pen.png"; // Importa el archivo PNG del icono del bolígrafo
import eraserIcon from "./eraser.png"; // Importa el archivo PNG del icono del borrador
import "./App.css";

function App() {
  const firstCanvas = useRef(null);
  const [images, setImages] = useState([]);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [dragStartPos, setDragStartPos] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [eraserActivated, setEraserActivated] = useState(false); // Estado para el borrador
  const [drawingEnabled, setDrawingEnabled] = useState(false); // Estado para habilitar el dibujo

  useEffect(() => {
    const handlePaste = (event) => {
      const items = (event.clipboardData || event.originalEvent.clipboardData).items;
      for (let index in items) {
        const item = items[index];
        if (item.kind === 'file') {
          const blob = item.getAsFile();
          const reader = new FileReader();
          reader.onload = (e) => {
            setImages(prevImages => [...prevImages, { src: e.target.result, x: 900, y: 0 }]);
          };
          reader.readAsDataURL(blob);
        }
      }
    };

    document.addEventListener('paste', handlePaste);

    return () => {
      document.removeEventListener('paste', handlePaste);
    };
  }, []);

  const handleMouseDown = (event, index) => {
    // Permitir el dibujo o el borrado si el bolígrafo o el borrador están seleccionados y el dibujo está habilitado
    if ((eraserActivated || !eraserActivated) && drawingEnabled) {
      setSelectedImageIndex(index);
      setDragStartPos({ x: event.clientX, y: event.clientY });
      setDragging(true);
    }
  };

  const handleMouseMove = (event) => {
    if (!eraserActivated && dragging && selectedImageIndex !== null) {
      const deltaX = event.clientX - dragStartPos.x;
      const deltaY = event.clientY - dragStartPos.y;
      setImages(prevImages => {
        const updatedImages = [...prevImages];
        updatedImages[selectedImageIndex].x += deltaX;
        updatedImages[selectedImageIndex].y += deltaY;
        return updatedImages;
      });
      setDragStartPos({ x: event.clientX, y: event.clientY });
    }

    // Borrado gradual con el borrador activado
    if (eraserActivated) {
      const canvas = firstCanvas.current.canvas.drawing;
      const ctx = canvas.getContext('2d');
      const x = event.clientX - canvas.getBoundingClientRect().left;
      const y = event.clientY - canvas.getBoundingClientRect().top;
      const radius = 20; // Radio del borrador

      if (dragging) {
        ctx.beginPath();
        ctx.arc(x, y, radius, 0, Math.PI * 2);
        ctx.closePath();
        ctx.globalCompositeOperation = 'destination-out'; // Borrado
        ctx.fill();
      }
    }
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  const clear = () => {
    firstCanvas.current.clear();
    setImages([]);
  };

  const undo = () => {
    firstCanvas.current.undo();
  };

  const downloadWithBlankBackground = () => {
    const canvas = firstCanvas.current.canvas.drawing;
    const tempCanvas = document.createElement('canvas');
    tempCanvas.width = canvas.width;
    tempCanvas.height = canvas.height;
    const tempCtx = tempCanvas.getContext('2d');

    // Dibujar fondo blanco en el canvas temporal
    tempCtx.fillStyle = '#ffffff'; // Color blanco
    tempCtx.fillRect(0, 0, tempCanvas.width, tempCanvas.height);

    // Dibujar el contenido del lienzo en el canvas temporal
    tempCtx.drawImage(canvas, 0, 0);

    // Dibujar las imágenes en el canvas temporal
    images.forEach(image => {
      const img = new Image();
      img.src = image.src;
      tempCtx.drawImage(img, image.x, image.y);
    });

    // Convertir el canvas temporal en una URL de datos en formato PNG
    const dataURL = tempCanvas.toDataURL('image/png');

    // Descargar la imagen
    downloadDataURL(dataURL, 'tablero.png');
  };

  const downloadDataURL = (dataURL, filename) => {
    const link = document.createElement('a');
    link.href = dataURL;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={{ position: 'relative' }}>
      <div
        style={{
          position: 'absolute',
          top: 10,
          left: 10,
          zIndex: 1000
        }}
      >
        <button onClick={clear}>
          Limpiar Dibujo
        </button>
        <button onClick={undo}>
          Deshacer
        </button>
        <button onClick={downloadWithBlankBackground}>
          Descargar imagen
        </button>
      </div>
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
        }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      />
      <CanvasDraw
        brushRadius={3}
        brushColor={eraserActivated ? 'white' : 'red'} // Color del pincel/borrador
        canvasWidth={1500} // Ancho del lienzo
        canvasHeight={1000} // Alto del lienzo
        hideGrid={false}
        style={{ border: '1px solid black' }}
        ref={firstCanvas}
        disabled={!drawingEnabled} // Deshabilita el lienzo si el dibujo no está habilitado
      />
      {images.map((image, index) => (
        <img
          key={index}
          src={image.src}
          alt="pasted"
          style={{
            position: 'absolute',
            left: image.x,
            top: image.y,
            cursor: 'pointer',
            border: selectedImageIndex === index ? '2px solid blue' : 'none'
          }}
          onMouseDown={(event) => handleMouseDown(event, index)}
        />
      ))}
      <div className="vertical-bar">
        {/* Icono del bolígrafo */}
        <img
          src={penIcon}
          alt="Pen Icon"
          className="icon"
          onClick={() => {
            // Lógica cuando se hace clic en el icono del bolígrafo
            setEraserActivated(false); // Desactivar el borrador
            setDrawingEnabled(true); // Habilitar el dibujo
          }}
        />
        {/* Icono del borrador */}
        <img
          src={eraserIcon}
          alt="Eraser Icon"
          className="icon"
          onClick={() => {
            // Lógica cuando se hace clic en el icono del borrador
            setEraserActivated(true); // Activar el borrador
            setDrawingEnabled(true); // Habilitar el dibujo para permitir el borrado
          }}
        />
      </div>
    </div>
  );
}

export default App;
