import React, { useRef, useEffect, useState } from "react";
import CanvasDraw from 'react-canvas-draw';
import Toolbar from './Toolbar';
import "./App.css";

function App() {
  const firstCanvas = useRef(null);
  const [images, setImages] = useState([]);
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [dragStartPos, setDragStartPos] = useState({ x: 0, y: 0 });
  const [dragging, setDragging] = useState(false);
  const [isErasing, setIsErasing] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);

  useEffect(() => {
    const handlePaste = (event) => {
      const items = (event.clipboardData || event.originalEvent.clipboardData).items;
      for (let index in items) {
        const item = items[index];
        if (item.kind === 'file') {
          const blob = item.getAsFile();
          const reader = new FileReader();
          reader.onload = (e) => {
            setImages(prevImages => [...prevImages, { src: e.target.result, x: 100, y: 100 }]); // Iniciar en 100, 100
          };
          reader.readAsDataURL(blob);
        }
      }
    };

    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === 'z') {
        event.preventDefault();
        undo();
      }
    };

    document.addEventListener('paste', handlePaste);
    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('paste', handlePaste);
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const handleMouseDown = (event, index) => {
    setSelectedImageIndex(index);
    setDragStartPos({ x: event.clientX, y: event.clientY });
    setDragging(true);
    event.stopPropagation(); // Detener la propagación del evento para evitar conflictos
  };

  const handleMouseMove = (event) => {
    if (dragging && selectedImageIndex !== null) {
      const deltaX = event.clientX - dragStartPos.x;
      const deltaY = event.clientY - dragStartPos.y;
      setImages(prevImages => {
        const updatedImages = [...prevImages];
        updatedImages[selectedImageIndex].x += deltaX;
        updatedImages[selectedImageIndex].y += deltaY;
        return updatedImages;
      });
      setDragStartPos({ x: event.clientX, y: event.clientY });
    }
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  const clear = () => {
    firstCanvas.current.clear();
    setImages([]);
    setIsDrawing(false); // Deshabilitar el dibujo después de limpiar
  };

  const undo = () => {
    firstCanvas.current.undo();
  };

  const downloadWithBlankBackground = () => {
    const canvas = firstCanvas.current.canvas.drawing;
    const tempCanvas = document.createElement('canvas');
    tempCanvas.width = canvas.width;
    tempCanvas.height = canvas.height;
    const tempCtx = tempCanvas.getContext('2d');

    // Dibujar fondo blanco en el canvas temporal
    tempCtx.fillStyle = '#ffffff'; // Color blanco
    tempCtx.fillRect(0, 0, tempCanvas.width, tempCanvas.height);

    // Dibujar el contenido del lienzo en el canvas temporal
    tempCtx.drawImage(canvas, 0, 0);

    // Dibujar las imágenes en el canvas temporal
    images.forEach(image => {
      const img = new Image();
      img.src = image.src;
      tempCtx.drawImage(img, image.x, image.y);
    });

    // Convertir el canvas temporal en una URL de datos en formato PNG
    const dataURL = tempCanvas.toDataURL('image/png');

    // Descargar la imagen
    downloadDataURL(dataURL, 'tablero.png');
  };

  const downloadDataURL = (dataURL, filename) => {
    const link = document.createElement('a');
    link.href = dataURL;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const toggleEraser = () => {
    setIsErasing(true);
    setIsDrawing(true); // Habilitar el dibujo al activar el borrador
  };

  const togglePencil = () => {
    setIsErasing(false);
    setIsDrawing(true); // Habilitar el dibujo al activar el lápiz
  };

  return (
    <div style={{ position: 'relative' }} onMouseMove={handleMouseMove} onMouseUp={handleMouseUp}>
      <Toolbar
        onClear={clear}
        onUndo={undo}
        onToggleEraser={toggleEraser}
        onDownload={downloadWithBlankBackground}
        onTogglePencil={togglePencil}
        isErasing={isErasing}
      />
      <CanvasDraw
        brushRadius={3}
        brushColor={isErasing ? 'white' : 'red'} // Color del pincel o borrador
        canvasWidth={1500} // Ancho del lienzo
        canvasHeight={1000} // Alto del lienzo
        hideGrid={false}
        disabled={!isDrawing} // Deshabilitar el dibujo si no está permitido
        style={{ border: '1px solid black' }}
        ref={firstCanvas}
      />
      {images.map((image, index) => (
        <img
          key={index}
          src={image.src}
          alt="pasted"
          className="pasted-image"
          style={{
            position: 'absolute',
            left: `${image.x}px`,
            top: `${image.y}px`,
            cursor: 'move',
            border: selectedImageIndex === index ? '2px solid blue' : 'none'
          }}
          onMouseDown={(event) => handleMouseDown(event, index)}
        />
      ))}
    </div>
  );
}

export default App;
