import React from 'react';
import { FaPencilAlt, FaEraser, FaUndo, FaTrashAlt, FaDownload } from 'react-icons/fa';
import './Toolbar.css';

const Toolbar = ({ onClear, onUndo, onToggleEraser, onDownload, onTogglePencil, isErasing }) => {
  const handleClear = (event) => {
    event.stopPropagation();
    onClear(event);
  };

  const handleUndo = (event) => {
    event.stopPropagation();
    onUndo(event);
  };

  const handleToggleEraser = (event) => {
    event.stopPropagation();
    onToggleEraser(event);
  };

  const handleDownload = (event) => {
    event.stopPropagation();
    onDownload(event);
  };

  const handleTogglePencil = (event) => {
    event.stopPropagation();
    onTogglePencil(event);
  };

  return (
    <div className="toolbar">
      <button
        className={`toolbar-button ${!isErasing ? 'active' : ''}`}
        onClick={handleTogglePencil}
      >
        <FaPencilAlt />
      </button>
      <button
        className={`toolbar-button ${isErasing ? 'active' : ''}`}
        onClick={handleToggleEraser}
      >
        <FaEraser />
      </button>
      <button className="toolbar-button" onClick={handleClear}>
        <FaTrashAlt />
      </button>
      <button className="toolbar-button" onClick={handleUndo}>
        <FaUndo />
      </button>
      <button className="toolbar-button" onClick={handleDownload}>
        <FaDownload />
      </button>
    </div>
  );
};

export default Toolbar;
