import React from 'react';
import { FaPencilAlt, FaEraser, FaUndo, FaTrashAlt, FaDownload } from 'react-icons/fa';
import './Toolbar.css';

const Toolbar = ({ onClear, onUndo, onToggleEraser, onDownload, onTogglePencil, activeTool }) => {
  return (
    <div className="toolbar">
      <button className={`toolbar-button ${activeTool === 'pencil' ? 'active' : ''}`} onClick={onTogglePencil}>
        <FaPencilAlt />
      </button>
      <button className={`toolbar-button ${activeTool === 'eraser' ? 'active' : ''}`} onClick={onToggleEraser}>
        <FaEraser />
      </button>
      <button className="toolbar-button" onClick={onClear}>
        <FaTrashAlt />
      </button>
      <button className="toolbar-button" onClick={onUndo}>
        <FaUndo />
      </button>
      <button className="toolbar-button" onClick={onDownload}>
        <FaDownload />
      </button>
    </div>
  );
};

export default Toolbar;
