import React from 'react';
import { FaPencilAlt, FaEraser, FaUndo, FaTrashAlt, FaDownload } from 'react-icons/fa';
import './Toolbar.css';

const Toolbar = ({ onClear, onUndo, onToggleEraser, onDownload, onTogglePencil, isErasing }) => {
  return (
    <div className="toolbar">
      <button
        className={`toolbar-button ${!isErasing ? 'active' : ''}`}
        onClick={(event) => {
          event.stopPropagation();
          onTogglePencil();
        }}
      >
        <FaPencilAlt />
      </button>
      <button
        className={`toolbar-button ${isErasing ? 'active' : ''}`}
        onClick={(event) => {
          event.stopPropagation();
          onToggleEraser();
        }}
      >
        <FaEraser />
      </button>
      <button className="toolbar-button" onClick={(event) => {
        event.stopPropagation();
        onClear(event);
      }}>
        <FaTrashAlt />
      </button>
      <button className="toolbar-button" onClick={(event) => {
        event.stopPropagation();
        onUndo(event);
      }}>
        <FaUndo />
      </button>
      <button className="toolbar-button" onClick={(event) => {
        event.stopPropagation();
        onDownload(event);
      }}>
        <FaDownload />
      </button>
    </div>
  );
};

export default Toolbar;
